import React from "react";
import { useForm } from 'react-hook-form';
import { Link  } from "react-router-dom";

// CSS
import './busca.css'

const Busca = () => {
    const db_clients = JSON.parse(localStorage.getItem('db_client')) ;
    const {register, handleSubmit} = useForm();
    const onSubmit= (data) => {
    const busca = data.busca;

    const filtro = (clients) => {
        const empresa = clients.empresa.toLowerCase();
        if(empresa.includes(busca)) {
            console.log(empresa)
            return empresa;

        }

    }
    var resultadoBusca = db_clients.filter(filtro)
    console.log(resultadoBusca)
    }




    return (
        <div className="Inicial">
            <h1 className="opening">
            Encontre oportunidades próximas de você e cresça a  sua rede de atuação!
            </h1>

            <form className="pesquisaHome" onSubmit={handleSubmit(onSubmit)} >
                <input className="form-home" id="buscaIput" placeholder="procure por negócios em nossa rede" type="text"  {...register("busca")} />
                    <button className="home-send" id="buscaEmpresas" ></button>

            </form>

            <div className="advanced">
                <Link to='/todos'> LISTA DE TODOS OS NEGÓCIOS
                </Link>
            </div>

            <div className="empresas_listadas">

            </div>
        </div>
        )
    }
export default Busca;
