import React from 'react';
import { useForm } from 'react-hook-form';
import { Link } from 'react-router-dom';

// CSS
import './busca.css';

const Busca = () => {
  const db_clients = JSON.parse(localStorage.getItem('db_client'));

  const { register, handleSubmit } = useForm();
  const onSubmit = (data) => {
    const busca = data.busca;
    const filtro = (clients) => {
      const empresa = clients.empresa.toLowerCase();
      if (empresa.includes(busca)) {
        console.log(empresa);
        return empresa;
      }
    };
    var resultadoBusca = db_clients.filter(filtro);
    console.log(resultadoBusca);

    const renderAllClients = (allClients, index) => {
      return (
        <>
          <section>
            <div className="dois">
              <div className="empresa">
                <h1>{allClients.empresa}</h1>
                <p className="distancia">está há 2km de você</p>
              </div>

              <div className="sobre">{allClients.sobre}</div>
            </div>
            <div className="flex">
              <div className="endereco">{allClients.endereco}</div>
              <div className="contato">email: {allClients.email}</div>
              <div className="contato">telefone: {allClients.telefone}</div>
            </div>
          </section>
        </>
      );
    };

    document.querySelector('.empresas_listadas').innerHTML =
      resultadoBusca.map(renderAllClients);
  };

  return (
    <div className="Inicial">
      <h1 className="opening">
        Encontre oportunidades próximas de você e cresça a sua rede de atuação!
      </h1>

      <form className="pesquisaHome" onSubmit={handleSubmit(onSubmit)}>
        <input
          className="form-home"
          id="buscaIput"
          placeholder="procure por negócios em nossa rede"
          type="text"
          {...register('busca')}
        />
        <button className="home-send" id="buscaEmpresas"></button>
      </form>

      <div className="advanced">
        <Link to="/todos"> LISTA DE TODOS OS NEGÓCIOS</Link>
      </div>

      <div className="empresas_listadas"></div>
    </div>
  );
};
export default Busca;
